import React, { useEffect, useRef, useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const ChessBoard = ({ moves }) => {
  const chessboardRef = useRef(null);
  const gameRef = useRef(new Chess());
  const timeoutRef = useRef(null);

  const [isPlaying, setIsPlaying] = useState(false);
  const [boardReady, setBoardReady] = useState(false);

  // Attendi che la scacchiera venga renderizzata
  useEffect(() => {
    const waitForBoard = () => {
      if (chessboardRef.current?.setPosition) {
        setBoardReady(true);
        console.log("‚úÖ Scacchiera pronta");
      } else {
        setTimeout(waitForBoard, 100); // Riprova ogni 100ms
      }
    };
    waitForBoard();
  }, []);

  const playOpening = () => {
    if (!boardReady || !chessboardRef.current) {
      console.warn("‚ùå La scacchiera non √® pronta");
      return;
    }

    console.log("üîÅ Inizio animazione apertura");
    const moveList = moves.split(" ").filter(Boolean);
    gameRef.current = new Chess();
    let i = 0;

    chessboardRef.current.setPosition("start");
    setIsPlaying(true);

    const playNext = () => {
      if (i >= moveList.length) {
        setIsPlaying(false);
        console.log("‚úÖ Animazione terminata");
        return;
      }

      const move = moveList[i];
      const result = gameRef.current.move(move);

      if (result) {
        chessboardRef.current.setPosition(gameRef.current.fen());
        i++;
        timeoutRef.current = setTimeout(playNext, 800);
      } else {
        console.error("‚ùå Mossa non valida:", move);
        setIsPlaying(false);
      }
    };

    setTimeout(playNext, 500);
  };

  useEffect(() => {
    return () => clearTimeout(timeoutRef.current);
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "2rem" }}>
      <Chessboard
        boardWidth={400}
        animationDuration={300}
        arePiecesDraggable={false}
        ref={chessboardRef}
      />
      <button
        onClick={playOpening}
        disabled={!boardReady || isPlaying}
        style={{
          marginTop: "1rem",
          padding: "0.5rem 1rem",
          fontSize: "1rem",
          backgroundColor: "#2563eb",
          color: "white",
          border: "none",
          borderRadius: "8px",
          cursor: !boardReady || isPlaying ? "not-allowed" : "pointer",
          opacity: !boardReady || isPlaying ? 0.6 : 1,
        }}
      >
        ‚ñ∂Ô∏è Ripeti Apertura
      </button>
    </div>
  );
};

export default ChessBoard;
