import React from "react";  
import { useParams } from "react-router-dom";   
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { selectOpening } from '../features/openings/OpeningSlice';  
import ChessBoard from "../components/ChessBoard"; 







function OpeningPage() { 
const { id } = useParams(); 
const dispatch = useDispatch(); 


const opening = useSelector((state) => state.openings.selectedOpening); 

useEffect(() => {
  dispatch(selectOpening(id));
}, [dispatch, id]);

if (!opening) return <p>Caricamento...</p>;


 return(
    <div>
        <h1>{opening.name}</h1>
      <p>Mosse iniziali: {opening.moves}</p> 
      <ChessBoard moves={opening.moves} />
      <p>{opening.description}</p>
    </div> 

 ); 




} 

export default OpeningPage;
