import React from "react";

function cleanPGNmoves(pgnString) {
   // Rimuove numeri di turno tipo 1., 2., 3... e eventuali simboli di punteggio (!, ?, ecc.)
   return pgnString
     .replace(/\d+\./g, "")         // rimuove "1.", "2.", "3.", ecc.
     .replace(/[!?+#=]+/g, "")      // rimuove simboli scacchistici (!, ?, +, #, =)
     .replace(/\s+/g, " ")          // rimuove spazi multipli
     .trim();                        // toglie spazi iniziali/finali
 }  

 export default cleanPGNmoves;
