import React, { useEffect, useRef, useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

const ChessBoard = ({ moves }) => {
  // Stato per la posizione attuale della scacchiera (FEN string)
  const [currentPosition, setCurrentPosition] = useState("start");
  const [isPlaying, setIsPlaying] = useState(false);

  // Riferimento per l'istanza del gioco e per il timeout
  const gameRef = useRef(new Chess());
  const timeoutRef = useRef(null);

  const playOpening = () => {
    console.log("üîÅ Inizio animazione apertura");
    const moveList = moves.split(" ").filter(Boolean);
    const game = new Chess(); // Usa una variabile locale per il ciclo di animazione
    gameRef.current = game;
    let i = 0;

    // Resetta la scacchiera alla posizione iniziale
    setCurrentPosition("start");
    setIsPlaying(true);

    const playNext = () => {
      if (i >= moveList.length) {
        setIsPlaying(false);
        console.log("‚úÖ Animazione terminata");
        return;
      }

      const move = moveList[i];
      const result = game.move(move);

      if (result) {
        // Aggiorna lo stato, che attiver√† l'animazione
        setCurrentPosition(game.fen());
        i++;
        timeoutRef.current = setTimeout(playNext, 800);
      } else {
        console.error("‚ùå Mossa non valida:", move);
        setIsPlaying(false);
      }
    };

    // Inizia l'animazione dopo un breve ritardo per mostrare la board iniziale
    timeoutRef.current = setTimeout(playNext, 500);
  };

  // Effetto per pulire il timeout se il componente viene smontato
  useEffect(() => {
    return () => clearTimeout(timeoutRef.current);
  }, []);

  return (
    <div style={{ textAlign: "center", marginTop: "2rem" }}>
      <Chessboard
        boardWidth={400}
        animationDuration={300}
        arePiecesDraggable={false}
        // Collega la posizione della scacchiera allo stato del componente
        position={currentPosition}
      />
      <button
        onClick={playOpening}
        disabled={isPlaying}
        style={{
          marginTop: "1rem",
          padding: "0.5rem 1rem",
          fontSize: "1rem",
          backgroundColor: "#2563eb",
          color: "white",
          border: "none",
          borderRadius: "8px",
          cursor: isPlaying ? "not-allowed" : "pointer",
          opacity: isPlaying ? 0.6 : 1,
        }}
      >
        ‚ñ∂Ô∏è Ripeti Apertura
      </button>
    </div>
  );
};

export default ChessBoard;
