import React from "react";
import ChessBoard from "../components/ChessBoard";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import cleanPGNmoves from "../utils/cleanPGNmoves"; 


function OpeningPage() {
  const { id } = useParams();
  const opening = useSelector((state) =>
    state.openings.find((o) => o.id === parseInt(id))
  );

  if (!opening) return <div>Apertura non trovata</div>;

  return (
    <div>
      <h1>{opening.name}</h1>
      <p>Mosse: {opening.moves}</p>
      <ChessBoard moves={cleanPGNmoves(opening.moves)} />
    </div>
  );
}

export default OpeningPage;
