import React from "react";
import ChessBoard from "../components/ChessBoard";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { cleanPNGMoves } from "../utils/cleanPNGMoves";   


function OpeningPage() {
  const { id } = useParams();
  const opening = useSelector((state) =>
    state.openings.list.find((o) => o.id === parseInt(id))
  );  

  console.log("Mosse grezze:", opening.moves);
console.log("Mosse pulite:", cleanPNGMoves(opening.moves)); 


  if (!opening) return <div>Apertura non trovata</div>;



  return (
    <div>
      <h1>La mia apertura</h1>

      {/* Mostra il componente ChessBoard solo se cleanedMoves non Ã¨ vuoto */}
      {cleanPNGMoves ? (
        <ChessBoard moves={cleanPNGMoves} />
      ) : (
        <p>Caricamento della scacchiera...</p> 
      )}
    </div>
  );
}

export default OpeningPage;
