import React, { useEffect, useState } from "react";
import Chessboard from "chessboardjsx";
import { Chess } from "chess.js";

const ChessBoard = ({ moves }) => {
  const [position, setPosition] = useState("start");
  const [error, setError] = useState(null); // aggiunto per mostrare messaggi

  useEffect(() => {
    const game = new Chess();
    const moveList = moves.split(" ");

    console.log("⏳ Mosse ricevute:", moveList);

    for (let i = 0; i < moveList.length; i++) {
      const result = game.move(moveList[i]);

      if (!result) {
        const errMsg = `❌ Errore alla mossa ${i + 1}: '${moveList[i]}' non è valida`;
        console.error(errMsg);
        setError(errMsg);
        setPosition("start");
        return;
      }
    }

    // Se tutto è andato bene, imposta la posizione corretta
    setPosition(game.fen());
    setError(null);
  }, [moves]);

  return (
    <div>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <Chessboard position={position} width={400} />
    </div>
  );
};

export default ChessBoard;
